    let provider = new ethers.providers.Web3Provider(ethereum);
    let signer = provider.getSigner();
    contract = new ethers.Contract(address, abi, signer);
  }

  return (
    <div className="App">
      <h1>Text Contract</h1>

      <button onClick={() => {
          if (contract && !connected) {
              ethereum.request({ method: 'eth_requestAccounts'})
                  .then(accounts => {
                      setConnected(true);
                  })
          }
      }}>{!connected ? 'Connect wallet' : 'Connected' }</button>

      <form onSubmit={(e) => {
        e.preventDefault();
        if (contract && connected) {
          contract.changeText(text)
            .then(() => {
              setText("");
            });
        }
      }}>
          <input type="text" placeholder="Enter text" onChange={e => setText(e.currentTarget.value)} value={text} />
          <input type="submit" value="Change text" />
      </form>

      <button onClick={() => {
        if (contract && connected) {
          contract.totalSupply().then((text) => {
            setSavedText(text);
          });
        }
      }}>Get text</button>

      <h3>{savedText}</h3>
    </div>
  );
}

export default App;
